const AddProposal = ({ newProposal, setNewProposal, addNewProposal, newProposalStatus }) => {
	const handleNewProposal = (e) => {
		setNewProposal(e.target.value);
	};
	return (
		<div>
			<h4>Add Proposa;</h4>
			<p>(only chairman can add new proposal)</p>
			<div
				style={{
					width: '15em',
					display: 'flex',
					justifyContent: 'space-between',
				}}>
				<input value={newProposal} onChange={handleNewProposal} />
				<button onClick={addNewProposal}>Add Proposal</button>
			</div>
			{/* {newVoterStatus && <p>Status: {newVoterStatus}</p>} */}
		</div>
	);
};

export default AddProposal;
